import React, {Component} from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation.js'
import Logo from './components/Logo/Logo.js'
import ImageLinkeForm from './components/ImageLinkeForm/ImageLinkeForm.js'
import Rank from './components/Rank/Rank.js'
import Particles from 'react-particles-js';
import Clarifai, { FACE_DETECT_MODEL } from 'clarifai' ;
import FaceRecognition from './components/FaceRecognition/FaceRecognition.js'
import SignIn from './components/SignIn/SignIn.js'

const app = new Clarifai.App({
  apiKey: '870f1255c44c4247b84caa340e399245'
});

const particleOption ={
  particles: {
    number: {
      value: 120,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
}



class App extends Component {
  constructor(){
    super();
    this.state={
      input: '',
      box:{},
      imageURL:'',
      route: 'signin'
    }
  }

  calculateFaceLocation = (data)=>{
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    console.log(clarifaiFace);
    const img = document.getElementById('input_image');
    // It is string originally, so we need to transfrom it into integer
    const width = Number(img.width);
    const height= Number(img.height);
    console.log(width, height);
    return{
      leftCol : clarifaiFace.left_col*width,
      topRow : clarifaiFace.top_row*height,
      // right is form right side 
      rightCol : width - clarifaiFace.right_col*width,
      // bottom is form bottom side 
      bottomRow : height - clarifaiFace.bottom_row*height
    }
  }


  displayFaceBox = (box)=>{
    this.setState({box: box});
  }

  onInputChange = (event)=>{
    this.setState({input: event.target.value});
  }

  onSubmit = ()=>{
    this.setState({imageURL: this.state.input});
    app.models.predict(FACE_DETECT_MODEL, this.state.input )
    .then(response =>{
        // Use Class here
      this.displayFaceBox(this.calculateFaceLocation(response)) }
    )
    .catch(err=> console.log("fail"));
  }
  onRouteChange = ()=>{
    this.setState({route: 'home'});
  }
  backRoute = ()=>{
    this.setState({ route: 'signin' });
  }

  render() {
    return (
      <div className="App">
        <Particles className ='particle'
          params={
            particleOption
          }
        />
        <Navigation backRoute = {this.backRoute}/>

        {this.state.route==='signin'
        // wrap then we can use javascript syntax
          ? <SignIn onRouteChange={this.onRouteChange}/>
          : <div>
          <Logo />
          <Rank />
          <ImageLinkeForm onInputChange={this.onInputChange} 
            Onsubmit = {this.onSubmit} />
          <FaceRecognition box={this.state.box} imageURL = {this.state.imageURL} />
          </div>
        }
      </div>
    )
  };
}

export default App;
