import React, {Component} from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation.js'
import Logo from './components/Logo/Logo.js'
import ImageLinkeForm from './components/ImageLinkeForm/ImageLinkeForm.js'
import Rank from './components/Rank/Rank.js'
import Particles from 'react-particles-js';
import Clarifai, { FACE_DETECT_MODEL } from 'clarifai' ;
import FaceRecognition from './components/FaceRecognition/FaceRecognition.js'


const app = new Clarifai.App({
  apiKey: '870f1255c44c4247b84caa340e399245'
});

const particleOption ={
  particles: {
    number: {
      value: 120,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
}



class App extends Component {
  constructor(){
    super();
    this.state={
      input: '',
      box:{}
    }
  }

  calculateFaceLocation = (data)=>{
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    const img = Document.getElementById('input_image');
    const width = Number(img.width);
  }

  onInputChange = (event)=>{
    this.setState({input: event.target.value});
  }

  onSubmit = ()=>{
    this.setState({imageURL: this.state.input});
    app.models.predict(FACE_DETECT_MODEL, this.state.input )
    .then(response =>{
        // Use Class here
        this.calculateFaceLocation(response)}
    )
    .catch(err=> console.log("fail"));
  }



  render() {
    return (
      <div className="App">
        <Particles className ='particle'
          params={
            particleOption
          }
        />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkeForm onInputChange={this.onInputChange} 
          Onsubmit = {this.onSubmit} />
        <FaceRecognition imageURL = {this.state.imageURL} />
      </div>
    )
  };
}

export default App;
